
// Fallback stub if real QRCode lib not loaded; displays payload text
if(typeof QRCode==='undefined'){
  window.QRCode = function(el, opts){
    var div = (el.tagName)? el : document.getElementById(el);
    div.innerHTML = '<div style="padding:10px;border:1px dashed #555;border-radius:8px;max-width:240px"><div style="font:12px monospace;white-space:pre-wrap;word-break:break-word">'+(opts.text||'QR')+'</div><div style="color:#94a3b8;margin-top:6px">QR lib non dispo â€” affichage texte</div></div>';
  };
  window.QRCode.CorrectLevel = {L:1,M:2,Q:3,H:4};
}
